#!/bin/shgit filter-branch --env-filter 'OLD_EMAIL="alan9999@163.com"CORRECT_NAME="ordinaryA"CORRECT_EMAIL="353458582@qq.com"if [ "$GIT_COMMITTER_EMAIL" = "$OLD_EMAIL" ]then    export GIT_COMMITTER_NAME="$CORRECT_NAME"    export GIT_COMMITTER_EMAIL="$CORRECT_EMAIL"fiif [ "$GIT_AUTHOR_EMAIL" = "$OLD_EMAIL" ]then    export GIT_AUTHOR_NAME="$CORRECT_NAME"    export GIT_AUTHOR_EMAIL="$CORRECT_EMAIL"fi' --tag-name-filter cat -- --branches --tags